  code = code.replace(
    /DelayExecution\s*\(\s*(\(\)\s*=>|function\s*\(\)\s*)\s*\{([\s\S]*?)\}\s*,\s*([^)]+)\)/g,
    "setTimeout(function() {$2}, $3)"
  );

  // Handle Unicode replacement characters more intelligently
  code = code.replace(/\uFFFD+/g, (match) => {
    // Try to decode common Unicode obfuscation patterns
    const length = match.length;
    if (length === 1) return '"unknown"';
    if (length === 2) return '"center"';
    if (length === 3) return '"left"';
    if (length === 4) return '"right"';
    return `"unknown_${length}_chars"`;
  });

  // Handle complex Unicode patterns with mixed characters
  code = code.replace(/\uFFFD[\u0000-\uFFFF]*\uFFFD/g, (match) => {
    // Try to extract meaningful content from mixed Unicode patterns
    const cleanContent = match.replace(/\uFFFD/g, '').trim();
    if (cleanContent.length > 0) {
      return `"${cleanContent}"`;
    }
    return match;
  });

  // Handle specific VWO event patterns
  code = code.replace(/\uFFFDt\uFFFD\uFFFD\uFFFD\uFFFDJ\x1A0/g, '"vwo_survey_display"');
  code = code.replace(/\uFFFDt\uFFFD\uFFFD\uFFFD\uFFFD\n\uFFFD\uFFFD\uFFFD\uFFFD^/g, '"vwo_survey_complete"');
  code = code.replace(/\uFFFDt\uFFFD\uFFFD\uFFFD\uFFFD\x02{0z\uFFFD\uFFFDnh\uFFFD\uFFFD\uFFFD/g, '"vwo_survey_questionSubmitted"');

  // Handle survey status patterns
