  code = code.replace(/\uFFFD\+\uFFFD/g, '"live"');
  code = code.replace(/\uFFFD\uFFFD\uFFFD\uFFFD/g, '"left"');
  code = code.replace(/\uFFFD\uFFFD\uFFFD/g, '"center"');
  code = code.replace(/\uFFFD\uFFFD/g, '"full"');
  code = code.replace(/\uFFFD\uFFFD\uFFFD\uFFFD\uFFFD/g, '"right"');
  code = code.replace(/\uFFFD\uFFFD\uFFFD\uFFFD\uFFFD\uFFFD/g, '"bottom"');
  code = code.replace(/\uFFFD\uFFFD\uFFFD\uFFFD\uFFFD\uFFFD\uFFFD/g, '"justify"');

  // Handle specific Unicode combinations in strings
  code = code.replace(/\uFFFD\uFFFD\uFFFD\{\$\uFFFDnh\uFFFD\uFFFD\uFFFD/g, '"question_submitted"');
