  code = code.replace(/\uFFFD\uFFFD\uFFFD\{ \uFFFD\uFFFD\uFFFD\^\uFFFD\uFFFD/g, '"survey_completed"');

  // Handle Unicode patterns in function calls and property names
  code = code.replace(/\uFFFD\uFFFD\uFFFD\uFFFD\uFFFD\uFFFD\uFFFD\uFFFD/g, '"function"');
